import React from 'react';
import ReactDOM from 'react-dom';
import { useEffect, useState } from 'react';
import { ethers } from 'ethers';

// Components
import Navigation from './components/Navigation';
import Search from './components/Search';
import Home from './components/Home';

// ABIs
import RealEstate from './abis/RealEstate.json'
import Escrow from './abis/Escrow.json'

// Config
import config from './config.json';

function App() {
  const [account, setAccount] =useState(null)
  const[provider,setProvider]=useState(null)

  const loadBlockachainData=async()=>{
    const provider=new ethers.providers.Web3Provider(window.ethereum)

    setProvider(provider)

    const network=await provider.getNetwork()

    // config[network.chainId].escrow.address
    const realEstate=new ethers.Contract( config[network.chainId].realEstate.address,RealEstate,provider)
    const escrow=new ethers.Contract( config[network.chainId].escrow.address,Es,provider)

    console.log(config[network.chainId].realEstate.address,config[network.chainId].escrow.address)
    window.ethereum.on('accountsChanged',async()=>{
      const accounts=await window.ethereum.request({method:'eth_requestAccounts'});
      const account=ethers.utils.getAddress(accounts[0]);

      setAccount(account)
    })
  }
  useEffect(()=>{
    loadBlockachainData();
  },[])
  return (
    <div>
      <Navigation account={account} setAccount={setAccount}/>
      <Search/>
      <div className='cards__section'>

        <h3>Welcome to Millow!</h3>

      </div>

    </div>
  );
}

export default App;
