import React from 'react';
import ReactDOM from 'react-dom';
import { useEffect, useState } from 'react';
import { ethers } from 'ethers';

// Components
import Navigation from './components/Navigation';
import Search from './components/Search';
import Home from './components/Home';

// ABIs
import RealEstate from './abis/RealEstate.json'
import Escrow from './abis/Escrow.json'

// Config
import config from './config.json';

function App() {
  const homesArray = [
    {
      "name": "Luxury NYC Penthouse",
      "address": "157 W 57th St APT 49B, New York, NY 10019",
      "description": "Penthouse located in the heart of NYC",
      "image": "https://ipfs.io/ipfs/QmQUozrHLAusXDxrvsESJ3PYB3rUeUuBAvVWw6nop2uu7c/1.png",
      "id": "1",
      "attributes": [
          {
              "trait_type": "Purchase Price",
              "value": 20
          },
          {
              "trait_type": "Type of Residence",
              "value": "Condo"
          },
          {
              "trait_type": "Bed Rooms",
              "value": 2
          },
          {
              "trait_type": "Bathrooms",
              "value": 3
          },
          {
              "trait_type": "Square Feet",
              "value": 2200
          },
          {
              "trait_type": "Year Built",
              "value": 2013
          }
      ]
  },
];
  const[homes,setHomes]=useState(homesArray)
  const [account, setAccount] =useState(null)
  const[provider,setProvider]=useState(null)
  const[escrow,setEscrow]=useState(null)
  console.log("a")
  const loadBlockachainData=async()=>{
    const provider=new ethers.providers.Web3Provider(window.ethereum)
    console.log("aaaaa")
    setProvider(provider)

    const network=await provider.getNetwork()

    // config[network.chainId].escrow.address
    const realEstate=new ethers.Contract( config[network.chainId].realEstate.address,RealEstate,provider)
    const totalSupply=await realEstate.total_Supply()
    var homes=[];
    // var prevHomes=[];
    // console.log(totalSupply)
    for(let i=2;i<=totalSupply;i++){
      const uri=await realEstate.tokenURI(i);
      const response=await fetch(uri)
      const metadata=await response.json()
      console.log(response)
      // homes.push(metadata);
      setHomes((prevHomes) => [
        ...prevHomes,
        metadata,
    ]);
    }
    // setHomes(homes)
    // console.log(homes)
    const escrow=new ethers.Contract( config[network.chainId].escrow.address,Escrow,provider)
    setEscrow(escrow);

    window.ethereum.on('accountsChanged',async()=>{
      const accounts=await window.ethereum.request({method:'eth_requestAccounts'});
      const account=ethers.utils.getAddress(accounts[0]);

      setAccount(account)
    })
  }
  useEffect(()=>{
    loadBlockachainData();
  },[])
  return (
    <div>
      <Navigation account={account} setAccount={setAccount}/>
      <Search/>
      <div className='cards__section'>

        <h3>Homes for You</h3>
          <hr/>
        <div className='cards'>

            {homes.map((home,index)=>( 

             <div className='card' key="index">
                <div className='card__image'>
                  <img src="home.image" alt="Home"/>
                </div>

              <div className='card__info'>
                <h4>${home.attributes[0].value}ETH</h4>
                <p>
                  <strong>${home.attributes[2].value}</strong> beds|
                  <strong>${home.attributes[3].value}</strong> bathrooms|
                  <strong>${home.attributes[4].value}</strong>  sqft
                </p>
                <p>${home.address}</p>
              </div>
            </div>
              ))}  
        </div>
      </div>

    </div>
  );
}

export default App;
